variable.value:
import java.util.regex.Matcher;
import java.util.regex.Pattern;
        
// Select the proper replacement String
switch (GUI_SET_TIMING_INTERFACE_TO) {
    case "DAHDI":
        _replacement = "res_timing_dahdi.so";
        break;
    case "timerfd":
        _replacement = "res_timing_timerfd.so";
        break;
    default:
        // Abort mission! Unknown value!
        errorOut = "GUI_SET_TIMING_INTERFACE_TO out of bound. Expecting {DAHDI, timerfd}, got " + GUI_SET_TIMING_INTERFACE_TO;
        return;
}       

// Prepare the Patterns for the default-modules.xml and modules.conf files
Pattern pXml = Pattern.compile("(?m)(\\Q<load>\\E)(\\Qres_timing_dahdi.so\\E|\\Qres_timing_timerfd.so\\E)(\\Q</load>\\E)");
Pattern pConf = Pattern.compile("(?m)^(\\Qload => \\E)(\\Qres_timing_dahdi.so\\E|\\Qres_timing_timerfd.so\\E)");

//Let's do this!
Matcher m = pXml.matcher(_contentXml);

if (m.find() ) {
    _contentXml = m.replaceAll("$1" + _replacement + "$3");
}

m.reset();

m = pConf.matcher(_contentConf);

if (m.find() ) {
    _contentConf = m.replaceAll("$1" + _replacement);
}
